    grok <span class="token punctuation">{</span>
      match <span class="token operator">=</span><span class="token operator">></span> <span class="token punctuation">[</span><span class="token string">"message"</span> <span class="token operator">=</span><span class="token operator">></span> <span class="token string">"%{IPORHOST:addre} %{USER:ident} %{USER:auth} \[%{HTTPDATE:timestamp}\] <span class="token entity" title="\&quot;">\"</span>%{WORD:http_method} %{NOTSPACE:request} HTTP/%{NUMBER:httpversion}<span class="token entity" title="\&quot;">\"</span> %{NUMBER:status} (?:%{NUMBER:bytes}|-) <span class="token entity" title="\&quot;">\"</span>(?:%{URI:http_referer}|-)<span class="token entity" title="\&quot;">\"</span> <span class="token entity" title="\&quot;">\"</span>%{GREEDYDATA:User_Agent}<span class="token entity" title="\&quot;">\"</span>"</span><span class="token punctuation">]</span>
      remove_field <span class="token operator">=</span><span class="token operator">></span> <span class="token punctuation">[</span><span class="token string">"message"</span><span class="token punctuation">]</span>
