import{_ as o,V as n,W as i,Z as t,Y as d,a0 as a,X as r,C as l}from"./framework-8eb1d601.js";const c={},s=r('<h1 id="linux的目录结构特点" tabindex="-1"><a class="header-anchor" href="#linux的目录结构特点" aria-hidden="true">#</a> Linux的目录结构特点</h1><br><h2 id="目录结构特点" tabindex="-1"><a class="header-anchor" href="#目录结构特点" aria-hidden="true">#</a> 目录结构特点</h2><br><ul><li><p>一切从根root 开始</p></li><li><p>Linux中每个设备可以挂在任何目录上面</p></li><li><p>Linux下面设备没有挂载无法使用</p></li><li><p>linux中一切皆文件</p></li></ul><br><h2 id="什么是挂载" tabindex="-1"><a class="header-anchor" href="#什么是挂载" aria-hidden="true">#</a> 什么是挂载？</h2><br>',8),h=r("<br><p><strong>举例：在 linux 系统下面使用光盘</strong><br><br></p><ol><li><p>把光盘放入光驱中</p></li><li><p>查看光盘 <code>ll /dev/cdrom</code></p></li><li><p>使用光盘 <code>cat</code> 乱码</p></li><li><p>把光盘挂载 <code>mount /dev/cdrom /mnt/ read-only</code> 只读 <code>df-h</code> 查看系统空间谁挂载路径</p></li><li><p>进入目录使用</p></li><li><p><code>cd /mnt</code> 实际上进入了光盘 可以看到光盘内容</p></li></ol><br><p>如果硬盘设备不挂载是没有访问入口的,就像房子没有大门，挂载就像给设备找了一个入口。</p><br><p><code>mount /dev/cdrom /mnt</code></p><p>设备 <strong>挂载点(mount point)</strong></p><br>",9),p=d("br",null,null,-1),u=r('<br><br><h2 id="linux-目录结构详解" tabindex="-1"><a class="header-anchor" href="#linux-目录结构详解" aria-hidden="true">#</a> Linux 目录结构详解</h2><br><table><thead><tr><th>目录名称</th><th>目录介绍</th></tr></thead><tbody><tr><td>/etc</td><td>配置文件</td></tr><tr><td>/home</td><td>用户的家目录，每一个用户的家目录通常默认为/home/USERNAME</td></tr><tr><td>/root</td><td>管理员的家目录</td></tr><tr><td>/lib</td><td>库文件 静态库：单在程序中的库，其他程序不能使用该库文件动态库：在内存中，任何用到该库的程序都可以使用</td></tr><tr><td>/lib/modules</td><td>内核模块文件</td></tr><tr><td>/media</td><td>挂载点目录，移动设备。在windows中，插入一张光盘，系统会自动读取光盘，用户可以直接执行，但在linux中，插入光盘后需要在挂载点挂载这个设备之后才可以使用这个设备</td></tr><tr><td>/mnt</td><td>挂载点目录，额外的临时文件系统</td></tr><tr><td>/opt</td><td>可选目录，第三方程序的安装目录</td></tr><tr><td>/proc</td><td>伪文件系统，内核映射文件</td></tr><tr><td>/sys</td><td>伪文件系统，跟硬件设备相关的属性映射文件</td></tr><tr><td>/tmp</td><td>临时文件</td></tr><tr><td>/var</td><td>可变化的文件，经常发生变化的文件</td></tr><tr><td>/bin</td><td>可执行文件，用户命令；其中用到的库文件可能在/lib，配置文件可能在/etc</td></tr><tr><td>/sbin</td><td>可执行文件，管理命令；其中用到的库文件可能在/lib，配置文件可能在/etc</td></tr><tr><td>/usr</td><td>只读文件，shared read-only</td></tr><tr><td>/usr/local</td><td>第三方软件</td></tr><tr><td>/boot</td><td>系统启动相关的文件，如内核、initrd，以及grub（BootLoader）</td></tr></tbody></table><p>​</p>',6),b={href:"http://www.cnblogs.com/forlive/p/8081515.html",target:"_blank",rel:"noopener noreferrer"};function m(_,x){const e=l("ExternalLinkIcon");return n(),i("div",null,[s,t(" 把苹果挂载到树上 `mount` 给磁盘分区 开了一个入口 进入到光盘中 入口=挂载点=目录"),h,t(" Linux下面设备(磁盘)不挂载是看不到入口的，没有窗户门的屋子，如果要设备被访问就必须有一个入口"),p,t("，这个入口就是挂载点挂载点实质就是目录。 "),u,d("p",null,[d("a",b,[t("详细原文"),a(e)])])])}const L=o(c,[["render",m],["__file","Linux-basics.html.vue"]]);export{L as default};
